import { useEffect, useState } from "react";

import About from "./About/About";
import Intro from "./Intro/Intro";
import Passion from "./Passion/Passion";
import Work from "./Work/Work";
import Contact from "./Contact/Contact";

import IntersectionContext from "../store/intersection-context";

import { useInView } from "react-intersection-observer";

const Section = (props: any) => {
  const [refIntro, inViewIntro] = useInView({
    /* Optional options */
    rootMargin: "-200px 0px 0px 0px",
  });
  const [refAbout, inViewAbout] = useInView({
    /* Optional options */
    rootMargin: "-200px 0px 0px 0px",
  });
  const [refPassion, inViewPassion] = useInView({
    /* Optional options */
    rootMargin: "200px 0px 0px 0px",
  });
  const [refWork, inViewWork] = useInView({
    /* Optional options */
    rootMargin: "0px 0px 0px 0px",
  });
  const [refContact, inViewContact] = useInView({
    /* Optional options */
    rootMargin: "200px 0px 0px 0px",
  });

  props.changeSection("about");

  console.log(inViewAbout, "about");
  console.log(inViewPassion, "passion");
  console.log(inViewWork, "Work");
  console.log(inViewContact, "Contact");
  console.log(inViewIntro, "Intro");

  if (inViewIntro) {
    props.changeSection("intro");
  } else if (inViewAbout) {
    props.changeSection("about");
  } else if (inViewPassion) {
    props.changeSection("passion");
  } else if (inViewWork) {
    props.changeSection("Work");
  } else if (inViewAbout) {
    props.changeSection("about");
  }

  return (
    <>
      <Intro ref={refIntro}></Intro>
      <About ref={refAbout}></About>
      <Passion ref={refPassion}></Passion>
      <Work ref={refWork}></Work>
      <Contact ref={refContact}></Contact>
    </>
  );
};

export default Section;
