import { useEffect, useState } from "react";

import About from "./About/About";
import Intro from "./Intro/Intro";
import Passion from "./Passion/Passion";
import Work from "./Work/Work";
import Contact from "./Contact/Contact";

import IntersectionContext from "../store/intersection-context";

import { useInView } from "react-intersection-observer";

const Section = (props: any) => {
  const [refAbout, inViewAbout] = useInView({
    /* Optional options */
    threshold: 0,
  });
  const [refIntro, inViewIntro] = useInView({
    /* Optional options */
    threshold: 0,
  });
  const [refPassion, inViewPassion] = useInView({
    /* Optional options */
    threshold: 0,
  });
  const [refWork, inViewWork] = useInView({
    /* Optional options */
    threshold: 0,
  });
  const [refContact, inViewContact] = useInView({
    /* Optional options */
    threshold: 0,
  });

  props.changeSection("about");

  console.log(inViewAbout, "section");
  return (
    <>
      <Intro></Intro>
      <About ref={refAbout}></About>
      <Passion></Passion>
      <Work></Work>
      <Contact></Contact>
    </>
  );
};

export default Section;
