import React, { useEffect, useRef, useState } from "react";

import styles from "./Navigation.module.css";
import NavigationItems from "./NavigationItems/NavigationItems";

const Navigation = (props: any) => {
  let listenerRef: any = useRef(null);
  const [isScrolled, setIsScrolled] = useState(false);
  const [section, setSection] = useState("intro");

  useEffect(() => {
    let listener = listenerRef.current;
    listener = document.addEventListener("scroll", () => {
      let scrolled = document.scrollingElement!.scrollTop;
      if (scrolled >= 70) {
        if (!isScrolled) {
          setIsScrolled(true);
        }
      } else {
        if (isScrolled) {
          setIsScrolled(false);
        }
      }

      if (scrolled < 620) {
        if (section !== "intro") {
          setSection("intro");
        }
      } else if (scrolled >= 620 && scrolled < 1400) {
        if (section !== "about") {
          setSection("about");
        }
      } else if (scrolled >= 1300 && scrolled < 1950) {
        if (section !== "work") {
          setSection("work");
        }
      } else if (scrolled >= 1950 && scrolled < 2700) {
        if (section !== "projects") {
          setSection("projects");
        }
      }
    });
    return () => {
      document.removeEventListener("scroll", listener);
    };
  }, [section, isScrolled]);

  return (
    <header className={isScrolled ? styles.Scrolled : styles.Top}>
      <div className={styles.Name}>
        <span>{section}</span>
        <span>Nicolas Theodarus</span>
        {/* <span>{section}</span> */}
      </div>
      <nav className={styles.DesktopOnly}>
        <NavigationItems section={section} currentSection={section} />
      </nav>
    </header>
  );
};

export default Navigation;
